import React from 'react';
import axios from 'axios';
import { useState, Component } from 'react';


class Signup extends Component {

    constructor(props) {
        super(props);
        this.state = {
          formData: {
            username: '',
            email: '',
            password: '',
            password2: '',
          },
          error: {
            name: 'Please Enter Name',
            email: 'Enter email',
            pass: 'Enter pass',
            conpass: 'enter confirm pass',
          },
          isFormValid: false,
        };
      }
    
      // Updates state.error if any in form fields
      handleInputChange(event) {
     
        let formData = this.state.formData;
        const {name, value} = event.target
        let error = this.state.error;
        formData[name] = value;
        switch(name){
          case 'username':
            error.name = value.length < 3
                    ? 'Name should have at least 3 characters'
                    : '';
            break;
    
          case 'email':
            error.email = !ValidEmailRegex.test(value)
                    ? 'Invalid Email'
                    : '';
            break;
    
          case 'password':
            error.pass = value.length < 7
                    ? 'Password should have at least 7 characters'
                    : '';
            break;
    
            case 'password2':
              error.conpass = value === formData.password
                      ? ''
                      : 'Confirm password does not match password';
              break;  
        }
    
        this.setState({
          formData: formData,
          error: error,
          isFormValid: !(error.name || error.email || error.pass || error.conpass)
        });
      }

    render() {

        return (
            <>
                <div className="sContainer">
                    <div className="sign-head">
                        <h3>Sign UP</h3>
                    </div>

                    <div className="sign-body">
                        <form onSubmit={this.handleClick}>
                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label for="inputfname">First Name</label>
                                    <input name="fName" type="text" value={this.state.formData.fName} onChange={this.handleInputChange} className="form-control" id="fn" placeholder="Enter first name" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputlname">Last Name</label>
                                    <input name="lName" type="text" value={this.state.formData.lName} onChange={this.handleInputChange} className="form-control" id="ln" placeholder="Enter last name" />
                                </div>
                                <div className="form-group col-md-12">
                                    <label for="inputemail">Email</label>
                                    <input name="email" type="email" className="form-control" id="inputemail" placeholder="Enter email" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputPassword4">Password</label>
                                    <input name="pass" type="password" className="form-control" id="inputPassword4" placeholder="Password" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputPassword5">Confirm Password</label>
                                    <input name="cpass" type="password" className="form-control" id="inputPassword5" placeholder="Re-enter password" />
                                </div>
                                <div className="form-group col-md-12">
                                    <label for="date">Date of Birth:</label>
                                    <input className="form-control" type="date" id="dob" name="dob" />

                                </div>
                                <div className="form-group col-md-12">
                                    <label for="gender">Gender:  </label>
                                    <span className="lastin">
                                        <input className="lastin" type="radio" id="m" name="gen" value="Male" />
                                        <label for="m">Male</label>
                                    </span>
                                    <span className="lastin">
                                        <input className="lastin" type="radio" id="f" name="gen" value="Female" />
                                        <label for="f">Female</label>
                                    </span>
                                    <span className="lastin">
                                        <input type="radio" id="o" name="gen" value="other" />
                                        <label for="o">Other</label>
                                    </span>
                                </div>
                            </div>
                            {/* ------------------------- */}
                            <div className="div-signup">
                                <button type="submit" className="btn btn-danger btn-signup sgbt1">Sign Up</button>
                            </div>
                            <div className="div-signup">
                                <button type="submit" className="btn btn-primary btn-signup sgbt2">Login</button>
                            </div>
                        </form>
                    </div>
                </div>
            </>
        );
    }
}

function RegisterButton(props) {
    const history = useHistory();
    
    function handleClick() {
      if(!props.isFormValid){
        let errors = props.error;
        var errorValues = Object.keys(errors).map(function(key){
          return errors[key];
         });
        props.createNotification (errorValues.join('\n'), 'error');
        return null;
      }
      let formData = props.formData;
      delete(formData['password2']);
      axios.post('blogs/api/register/', formData).then(res => {
        if (res.data.error){
          props.createNotification(res.data.error, 'error');
        }
        else{
          props.createNotification("Registered. Now login", 'success')
          history.push('../')
          history.push('../login');
        }
  
      })    
    }
  
    return (
      <button type="button" className="btn" onClick={handleClick}>
        Register
      </button>
    );
  }

export { Signup };
