import React from 'react';
import axios from 'axios';
import { useState, Component } from 'react';


class Signup extends Component {

    constructor(props) {
        super(props);
        this.state = {
            formData: {
                fName: '',
                lName: '',
                // email: '',
                // username: '',
                // email: '',
                // password: '',
                // password2: '',
            }
        };
        this.handleInputChange = this.handleInputChange.bind(this);
    };

    // Updates state.error if any in form fields
    handleInputChange(title, event) {
        let formData = this.state.formData;
        const { name, value } = event.target
        // let error = this.state.error;
        formData[name] = value;

        this.setState({
            formData: formData,
             });
    };

    handleClick(e) {
        // event.preventDefault();
        // let temp = e.target.formData;
        let temp=this.state.formData;
        axios.post('http://localhost:8000', temp).then(res => {
        console.log("I am printing");    
        console.log(res);
        })

    };

    render() {

        return (
            <>
                <div className="sContainer">
                    <div className="sign-head">
                        <h3>Sign UP</h3>
                    </div>

                    <div className="sign-body">
                        <form onSubmit={this.handleClick}>
                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label for="inputfname">First Name</label>
                                    <input name="fName" type="text" value={this.state.formData.fName} onChange={this.handleInputChange.bind(this, 'fName')} className="form-control" id="fn" placeholder="Enter first name" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputlname">Last Name</label>
                                    <input name="lName" type="text" value={this.state.formData.lName} onChange={this.handleInputChange.bind(this, 'lName')} className="form-control" id="ln" placeholder="Enter last name" />
                                </div>
                                <div className="form-group col-md-12">
                                    <label for="inputemail">Email</label>
                                    <input name="email" type="email" className="form-control" id="inputemail" placeholder="Enter email" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputPassword4">Password</label>
                                    <input name="pass" type="password" className="form-control" id="inputPassword4" placeholder="Password" />
                                </div>
                                <div className="form-group col-md-6">
                                    <label for="inputPassword5">Confirm Password</label>
                                    <input name="cpass" type="password" className="form-control" id="inputPassword5" placeholder="Re-enter password" />
                                </div>
                                <div className="form-group col-md-12">
                                    <label for="date">Date of Birth:</label>
                                    <input className="form-control" type="date" id="dob" name="dob" />

                                </div>
                                <div className="form-group col-md-12">
                                    <label for="gender">Gender:  </label>
                                    <span className="lastin">
                                        <input className="lastin" type="radio" id="m" name="gen" value="Male" />
                                        <label for="m">Male</label>
                                    </span>
                                    <span className="lastin">
                                        <input className="lastin" type="radio" id="f" name="gen" value="Female" />
                                        <label for="f">Female</label>
                                    </span>
                                    <span className="lastin">
                                        <input type="radio" id="o" name="gen" value="other" />
                                        <label for="o">Other</label>
                                    </span>
                                </div>
                            </div>
                            {/* ------------------------- */}
                            <div className="div-signup">
                                <button type="submit" className="btn btn-danger btn-signup sgbt1">Sign Up</button>
                            </div>
                            <div className="div-signup">
                                <button type="submit" className="btn btn-primary btn-signup sgbt2">Login</button>
                            </div>
                        </form>
                    </div>
                </div>
            </>
        );
    }
}





export { Signup };
